import React from 'react';
import { useLocation } from 'react-router-dom';

function Dashboard() {
    const { items, fetchItems } = useApi();

    useEffect(() => {
        fetchItems();
    }, [fetchItems]);

    return (
        <div>
            <h1>Admin Dashboard</h1>
            <ul>
                {items.map(item => (
                    <li key={item._id}>
                        {item.name} - In Stock: {item.stock}
                        {/* Add button or form to update stock here */}
                    </li>
                ))}
            </ul>
            {/* Additional features to add/edit items */}
        </div>
    );
}

export default Dashboard;
