import React, { useState, useEffect, useCallback } from 'react';
import { fetchItems, updateItem, fetchOrders } from '../services/ApiService';
import { useAuth } from '../context/authContext';

function AdminPanel() {
    const [items, setItems] = useState([]);
    const [orders, setOrders] = useState([]);
    const { user } = useAuth();
    const [loading, setLoading] = useState(false);

    const loadData = useCallback(async () => {
        if (user?.token) {
            setLoading(true);
            console.log('Fetching items and orders with token:', user.token);
            try {
                const fetchedItemsResponse = await fetchItems(user.token);
                const fetchedOrdersResponse = await fetchOrders(user.token);
                setItems(fetchedItemsResponse.data);
                setOrders(fetchedOrdersResponse.data);
                console.log('Items:', fetchedItemsResponse.data);
                console.log('Orders:', fetchedOrdersResponse.data);
            } catch (error) {
                console.error('Failed to fetch data:', error);
            } finally {
                setLoading(false);
            }
        } else {
            console.log('No token found, unable to fetch data.');
        }
    }, [user?.token]);  // Dependency on user.token to refetch when it changes

    useEffect(() => {
        loadData();
    }, [loadData]); // loadData included as a dependency

    const handleStockChange = async (itemId, newStock) => {
        if (newStock >= 0) {
            try {
                await updateItem(itemId, { stock: newStock }, user?.token);
                setItems(prevItems => prevItems.map(item =>
                    item._id === itemId ? { ...item, stock: newStock } : item
                ));
                console.log('Stock updated for item ID:', itemId);
            } catch (error) {
                console.error('Failed to update item:', error);
            }
        }
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            <button onClick={loadData} disabled={loading}>
                {loading ? 'Loading...' : 'Refresh Data'}
            </button>
            {loading && <p>Loading...</p>}
            {!loading && (
                <>
                    <h2>Items</h2>
                    <ul>
                        {items.map(item => (
                            <li key={item._id}>
                                {item.title} - Stock: {item.stock}
                                <button onClick={() => handleStockChange(item._id, item.stock + 1)}>Increase</button>
                                <button onClick={() => handleStockChange(item._id, item.stock - 1)}>Decrease</button>
                            </li>
                        ))}
                    </ul>
                    <h2>Orders</h2>
                    <ul>
                        {orders.map(order => (
                            <li key={order._id}>
                                Order ID: {order._id} - Total: ${order.total}, User ID: {order.userId}
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
}

export default AdminPanel;
