import React, { useEffect, useState } from 'react';
import { useAuth } from '../context/authContext';
import { fetchItems, fetchOrders } from '../services/ApiService';

function AdminDashboard() {
    const { token } = useAuth();
    const [items, setItems] = useState([]);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadData = async () => {
            setLoading(true);
            try {
                const itemsResponse = await fetchItems(token);
                const ordersResponse = await fetchOrders(token);
                console.log(itemsResponse.data);  // Check what's logged in the console
                console.log(ordersResponse.data); // Check what's logged in the console
                setItems(itemsResponse.data);
                setOrders(ordersResponse.data);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
            setLoading(false);
        };

        loadData();
    }, [token]);

    if (loading) return <p>Loading...</p>;
    if (!items.length && !orders.length) return <p>No data to display.</p>;

    return (
        <div>
            <h1>Admin Dashboard</h1>
            <h2>Items</h2>
            <ul>
                {items.map(item => (
                    <li key={item._id}>
                        {item.title} - Stock: {item.stock}
                    </li>
                ))}
            </ul>
            <h2>Orders</h2>
            <ul>
                {orders.map(order => (
                    <li key={order._id}>
                        Order ID: {order._id} - Total: ${order.total}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default AdminDashboard;
