import React, { useEffect, useState } from 'react';
import { fetchItems } from '../services/apiService';

function ItemList({ variant }) {
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        setLoading(true);
        fetchItems()
            .then(response => {
                console.log('API Data:', response.data); // Check what is actually being set
                setItems(response.data.data);
                setLoading(false);
            })
            .catch(error => {
                setError('Failed to fetch items: ' + error.message);
                setLoading(false);
            });
    }, []);

    if (loading) return <p>Loading items...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <h1>{variant === 'product' ? 'Product List' : 'Items Available'}</h1>
            {Array.isArray(items) ? (
                items.map(item => (
                    <div key={item._id} style={variant === 'product' ? { listStyleType: 'none' } : null}>
                        <h3>{item.title}</h3>
                        <img src={item.image} alt={item.title} style={{ width: '100px', height: '100px' }}/>
                        <p>Manufacturer: {item.manufacturer}</p>
                        <p>Price: ${item.price}</p>
                    </div>
                ))
            ) : <p>Items is not an array</p>}
        </div>
    );
}

export default ItemList;
