import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import PaymentModal from './PaymentModal';
import { createOrder } from '../services/ApiService';  // Assuming this function exists
import './Checkout.css';

function Checkout() {
    const { cart } = useCart();
    const [showModal, setShowModal] = useState(false);

    const handleOrderSubmission = async (paymentDetails) => {
        // Prepare the order data
        const orderData = {
            items: cart.items,
            paymentDetails,
            shippingAddress: paymentDetails.shippingAddress,
            email: paymentDetails.email
        };

        try {
            const response = await createOrder(orderData);
            alert('Order processed successfully!');
            // You might want to clear the cart here or navigate the user to a confirmation page
        } catch (error) {
            alert('Failed to process order: ' + error.message);
        }
    };

    return (
        <div className="checkout-container">
            <h1>Checkout</h1>
            <button onClick={() => setShowModal(true)}>Proceed to Checkout</button>
            {showModal && <PaymentModal onSubmit={handleOrderSubmission} onClose={() => setShowModal(false)} />}
            {/* Other checkout contents */}
        </div>
    );
}

export default Checkout;
