import React, { createContext, useContext, useReducer } from 'react';

const CartContext = createContext();

const cartReducer = (state, action) => {
    switch (action.type) {
        case 'ADD_ITEM':
            // Check if item already exists
            const existingItem = state.items.find(item => item._id === action.payload._id);
            const updatedItems = existingItem
                ? state.items.map(item =>
                    item._id === action.payload._id ? { ...item, quantity: item.quantity + 1 } : item
                )
                : [...state.items, { ...action.payload, quantity: 1 }];
            return {
                ...state,
                items: updatedItems,
                discount: updatedItems.length > 1 // Apply discount if more than one unique item
            };
        case 'REMOVE_ITEM':
            const filteredItems = state.items.filter(item => item._id !== action.payload);
            return {
                ...state,
                items: filteredItems,
                discount: filteredItems.length > 1
            };
        case 'CLEAR_CART':
            return { items: [], discount: false };
        default:
            return state;
    }
};

const initializeCart = () => {
    return { items: [], discount: false };
};

export const CartProvider = ({ children }) => {
    const [state, dispatch] = useReducer(cartReducer, initializeCart());

    const addItem = item => dispatch({ type: 'ADD_ITEM', payload: item });
    const removeItem = itemId => dispatch({ type: 'REMOVE_ITEM', payload: itemId });
    const clearCart = () => dispatch({ type: 'CLEAR_CART' });

    return (
        <CartContext.Provider value={{ cart: state, addItem, removeItem, clearCart }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => useContext(CartContext);
