import React, { useState, useEffect } from 'react';
import { fetchItems, updateItem, deleteItem, fetchOrders } from '../services/ApiService';
import { useAuth } from '../context/authContext';

function AdminPanel() {
    const [items, setItems] = useState([]);
    const [orders, setOrders] = useState([]);
    const { token } = useAuth();

    useEffect(() => {
        async function loadData() {
            if (token) {
                const fetchedItemsResponse = await fetchItems(token);
                const fetchedOrdersResponse = await fetchOrders(token);
                console.log("Fetched Items:", fetchedItemsResponse.data);
                console.log("Fetched Orders:", fetchedOrdersResponse.data);
                setItems(fetchedItemsResponse.data);
                setOrders(fetchedOrdersResponse.data);
            }
        }
        loadData();
    }, [token]);

    return (
        <div>
            <h1>Admin Panel</h1>
            <h2>Items</h2>
            {items.map(item => (
                <p key={item._id}>{item.title} - Stock: {item.stock}</p>
            ))}
            <h2>Orders</h2>
            {orders.map(order => (
                <div key={order._id}>
                    <p>Order ID: {order._id}</p>
                    {order.items.map(item => (
                        <p key={item._id}>
                            Item ID: {item.item}, Quantity: {item.quantity}
                        </p>
                    ))}
                </div>
            ))}
        </div>
    );
}

export default AdminPanel;
