import React, { useState } from 'react';
import './PaymentModal.css';

function PaymentModal({ onSubmit, onClose }) {
    const [email, setEmail] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [cvv, setCvv] = useState('');
    const [expirationDate, setExpirationDate] = useState('');
    const [shippingAddress, setShippingAddress] = useState('');

    const handleSubmission = () => {
        if (!/^\S+@\S+\.\S+$/.test(email) ||
            !/^\d{16}$/.test(cardNumber) ||
            !/^\d{3}$/.test(cvv) ||
            !expirationDate ||
            !shippingAddress) {
            alert('Please enter valid payment information.');
            return;
        }
        onSubmit({ email, cardNumber, cvv, expirationDate, shippingAddress });
    };

    return (
        <div className="modal-overlay">
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                <span className="close" onClick={onClose}>&times;</span>
                <div className="payment-form">
                    <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email" />
                    <input type="text" maxLength="16" value={cardNumber} onChange={e => setCardNumber(e.target.value)} placeholder="Card Number" />
                    <input type="text" maxLength="3" value={cvv} onChange={e => setCvv(e.target.value)} placeholder="CVV" />
                    <input type="date" value={expirationDate} onChange={e => setExpirationDate(e.target.value)} />
                </div>
                <textarea className="shipping-address" value={shippingAddress} onChange={e => setShippingAddress(e.target.value)} placeholder="Shipping Address"></textarea>
                <button className="process-order-btn" onClick={handleSubmission}>Process Order</button>
            </div>
        </div>
    );
}

export default PaymentModal;
