import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './context/authContext'; // Import the useAuth hook
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import AdminPanel from './components/AdminPanel';
import Login from './components/Login';
import Register from './components/Register';
import ProtectedRoute from './routes/ProtectedRoute';
import AdminRoute from './routes/AdminRoute';
import AddItem from './components/AddItem';
import ItemsList from './components/ItemsList';
import './App.css';

function App() {
    const { isAuthenticated, user } = useAuth(); // Use the auth context

    return (
        <BrowserRouter>
            <div className="App">
                <Header/>
                <Routes>
                    {/* Redirect base route depending on authentication status */}
                    <Route path="/" element={isAuthenticated ? (user.isAdmin ? <Navigate to="/admin" /> : <Navigate to="/dashboard" />) : <Navigate to="/login" />} />

                    {/* Public routes */}
                    <Route path="/login" element={<Login/>}/>
                    <Route path="/register" element={<Register/>}/>

                    {/* Protected routes for authenticated users */}
                    <Route path="/dashboard" element={<ProtectedRoute><Dashboard/></ProtectedRoute>}/>
                    <Route path="/items" element={<ProtectedRoute><ItemsList/></ProtectedRoute>}/>

                    {/* Admin-only routes */}
                    <Route path="/admin" element={<AdminRoute><AdminPanel/></AdminRoute>}/>
                    <Route path="/add-item" element={<AdminRoute><AddItem/></AdminRoute>}/>

                    {/* Fallback for unmatched routes */}
                    <Route path="*" element={<Navigate to="/" />} />
                </Routes>
            </div>
        </BrowserRouter>
    );
}

export default App;
