import React from 'react';
import './ReviewsDisplay.css'; // Assuming you have corresponding CSS for styling

function ReviewsDisplay({ reviews }) {
    if (!reviews || reviews.length === 0) return <p>No reviews yet.</p>;

    return (
        <div className="reviews-container">
            {reviews.map(review => (
                <div key={review._id} className="review-card">
                    <div className="rating">
                        {/* Render stars based on rating */}
                        {[...Array(review.rating)].map((_, index) => (
                            <span key={index} className="star">&#9733;</span>
                        ))}
                    </div>
                    <p className="comment">{review.comment}</p>
                    <p className="reviewer">By: {review.user.username}</p>
                </div>
            ))}
        </div>
    );
}

export default ReviewsDisplay;
