import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import PaymentModal from './PaymentModal';
import './Checkout.css';

function Checkout() {
    const { cart } = useCart();
    const [showModal, setShowModal] = useState(false);

    const calculateTotal = () => {
        return cart.items.reduce((acc, item) => acc + item.price * item.quantity, 0).toFixed(2);
    };

    const handlePayment = () => {
        setShowModal(true);
    };

    return (
        <div className="checkout-container">
            <h1>Checkout</h1>
            {cart.items.length > 0 ? (
                <div>
                    <ul className="checkout-list">
                        {cart.items.map((item) => (
                            <li key={item._id} className="checkout-item">
                                <div className="item-image-container">
                                    <img src={item.image} alt={item.title} className="checkout-item-image"/>
                                </div>
                                <div className="item-details">
                                    <h3>{item.title}</h3>
                                    <p>Quantity: {item.quantity}</p>
                                    <p>Price: ${item.price.toFixed(2)}</p>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <div className="checkout-total">
                        <strong>Total: ${calculateTotal()}</strong>
                    </div>
                    <div className="pay-now-container">
                        <button className="pay-now-btn" onClick={handlePayment}>Pay Now</button>
                    </div>
                    {showModal && <PaymentModal onSubmit={handlePayment} onClose={() => setShowModal(false)} />}
                </div>
            ) : (
                <p>Your cart is empty.</p>
            )}
        </div>
    );
}

export default Checkout;
