import React, { useState, useEffect } from 'react';
import { fetchItems, updateItem, fetchOrders } from '../services/ApiService';
import { useAuth } from '../context/authContext';

function AdminPanel() {
    const [items, setItems] = useState([]);
    const [orders, setOrders] = useState([]);
    const { user, token } = useAuth();

    useEffect(() => {
        async function loadData() {
            if (token) {
                try {
                    const fetchedItemsResponse = await fetchItems(token);
                    const fetchedOrdersResponse = await fetchOrders(token);
                    setItems(fetchedItemsResponse.data);
                    setOrders(fetchedOrdersResponse.data);
                } catch (error) {
                    console.error('Failed to fetch data:', error);
                }
            } else {
                console.log('No token found, unable to fetch data.');
            }
        }
        loadData();
    }, [token]);

    return (
        <div>
            <h1>Admin Panel</h1>
            <h2>Orders</h2>
            {orders.length > 0 ? (
                <ul>
                    {orders.map(order => (
                        <li key={order._id}>
                            <strong>Order ID:</strong> {order._id}, <strong>Total:</strong> ${order.total}, <strong>User:</strong> {order.user.username}
                            <ul>
                                {order.items.map(({ item, quantity }) => (
                                    <li key={item._id}>
                                        <strong>Item:</strong> {item.title}, <strong>Price:</strong> ${item.price}, <strong>Quantity:</strong> {quantity}
                                    </li>
                                ))}
                            </ul>
                        </li>
                    ))}
                </ul>
            ) : <p>No orders to display.</p>}
            <h2>Items</h2>
            {items.length > 0 ? (
                <ul>
                    {items.map(item => (
                        <li key={item._id}>
                            <strong>{item.title}</strong> - In Stock: {item.stock}
                        </li>
                    ))}
                </ul>
            ) : <p>No items to display.</p>}
        </div>
    );
}

export default AdminPanel;
