import React from 'react';

function PaymentModal({ onSubmit, onClose }) {
    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={e => e.stopPropagation()}> {/* Prevents click inside the modal from closing it */}
                <h2>Payment Information</h2>
                <form onSubmit={(e) => {
                    e.preventDefault();
                    onSubmit(/* Pass payment data back */);
                }}>
                    {/* Payment fields */}
                    <input type="text" placeholder="Card Number" required />
                    <input type="text" placeholder="CVV" required />
                    <input type="month" placeholder="Expiration Date" required />
                    <input type="email" placeholder="Email Address" required />
                    <input type="text" placeholder="Shipping Address" required />
                    <button type="submit">Submit Payment</button>
                </form>
                <button onClick={onClose}>Close</button>
            </div>
        </div>
    );
}

export default PaymentModal;
