import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import PaymentModal from './PaymentModal';
import { createOrder } from '../services/ApiService';

function Checkout() {
    const { cart, clearCart } = useCart();
    const [showModal, setShowModal] = useState(false);
    const navigate = useNavigate();

    const handleOrderSubmission = async (paymentDetails) => {
        try {
            const response = await createOrder({
                items: cart.items,
                paymentDetails,
                shippingAddress: paymentDetails.shippingAddress,
                email: paymentDetails.email,
                userId: paymentDetails.userId // Ensure userId is passed along
            });
            clearCart();
            navigate('/confirmation', { state: { orderDetails: response.data.order } });
        } catch (error) {
            console.error('Failed to process order:', error);
        }
    };

    return (
        <div className="checkout-container">
            <h1>Checkout</h1>
            <button onClick={() => setShowModal(true)}>Proceed to Checkout</button>
            {showModal && (
                <PaymentModal onSubmit={handleOrderSubmission} onClose={() => setShowModal(false)} />
            )}
        </div>
    );
}

export default Checkout;
