import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [authState, setAuthState] = useState({
        token: localStorage.getItem('token'),
        user: JSON.parse(localStorage.getItem('user')),
        isAuthenticated: Boolean(localStorage.getItem('token')),
    });

    useEffect(() => {
        // This effect will sync the auth state with local storage whenever the component mounts.
        const syncLocalStorageWithState = () => {
            const token = localStorage.getItem('token');
            const user = JSON.parse(localStorage.getItem('user'));
            if (token && user) {
                setAuthState({ token, user, isAuthenticated: true });
            }
        };

        syncLocalStorageWithState();

        // Optionally listen to storage events if changes are made in different tabs
        window.addEventListener('storage', syncLocalStorageWithState);

        return () => {
            window.removeEventListener('storage', syncLocalStorageWithState);
        };
    }, []);

    const login = (token, user) => {
        localStorage.setItem('token', token);
        localStorage.setItem('user', JSON.stringify(user));
        setAuthState({
            token,
            isAuthenticated: true,
            user
        });
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setAuthState({ token: null, user: null, isAuthenticated: false });
    };

    return (
        <AuthContext.Provider value={{ ...authState, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
