import React from 'react';
import { useLocation } from 'react-router-dom';

function Confirmation() {
    const location = useLocation();
    const orderDetails = location.state?.orderDetails;  // Use optional chaining to avoid errors

    // Handle case where orderDetails might not be defined
    if (!orderDetails) {
        return <div>No order details available. Please check your order confirmation link.</div>;
    }

    return (
        <div>
            <h1>Order Confirmation</h1>
            <p>Thank you for your order, {orderDetails.email}!</p>
            <p>Order Number: {orderDetails._id}</p>
            <p>Shipping to: {orderDetails.shippingAddress}</p>
            <ul>
                {orderDetails.items && orderDetails.items.map(item => (
                    <li key={item._id}>{item.title} - Quantity: {item.quantity}</li>
                ))}
            </ul>
            <p>Total Cost: ${orderDetails.totalCost}</p>
        </div>
    );
}

export default Confirmation;
