import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/authContext'; // Ensure you import AuthProvider and useAuth
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import AdminPanel from './components/AdminPanel';
import Login from './components/Login';
import Register from './components/Register';
import ProtectedRoute from './routes/ProtectedRoute';
import AdminRoute from './routes/AdminRoute';
import AddItem from './components/AddItem';
import ItemsList from './components/ItemsList';
import './App.css';

function App() {
    return (
        <AuthProvider>
            <BrowserRouter>
                <div className="App">
                    <Header />
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        <Route path="/add-item" element={<AdminRoute><AddItem /></AdminRoute>} />
                        <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>} />
                        <Route path="/admin" element={<AdminRoute><AdminPanel /></AdminRoute>} />
                        <Route path="/login" element={<LoginPage />} />
                        <Route path="/register" element={<RegisterPage />} />
                    </Routes>
                </div>
            </BrowserRouter>
        </AuthProvider>
    );
}

function HomePage() {
    const { isAuthenticated } = useAuth();
    // Redirect to dashboard if logged in, show public home otherwise
    return isAuthenticated ? <Navigate to="/dashboard" /> : <ItemsList />;
}

function LoginPage() {
    const { isAuthenticated } = useAuth();
    // Redirect to dashboard if already logged in
    return isAuthenticated ? <Navigate to="/dashboard" /> : <Login />;
}

function RegisterPage() {
    const { isAuthenticated } = useAuth();
    // Redirect to dashboard if already logged in
    return isAuthenticated ? <Navigate to="/dashboard" /> : <Register />;
}

export default App;
