import React from 'react';
import { useCart } from '../context/CartContext';
import './Checkout.css';  // Make sure your CSS file is linked

function Checkout() {
    const { cart } = useCart();  // Access the cart from the context

    // Function to calculate the total price of all items in the cart
    const calculateTotal = () => {
        return cart.items.reduce((acc, item) => acc + item.quantity * item.price, 0).toFixed(2);
    };

    return (
        <div className="checkout-container">
            <h1>Checkout</h1>
            {cart.items.length > 0 ? (
                <div>
                    <ul className="checkout-list">
                        {cart.items.map((item, index) => (
                            <li key={item._id} className="checkout-item">
                                <div className="item-details">
                                    <h3>ITEM {index + 1}</h3>
                                    <p>Quantity: {item.quantity}</p>
                                    <p>Price: ${item.price.toFixed(2)}</p>
                                </div>
                            </li>
                        ))}
                    </ul>
                    <div className="checkout-total">
                        <strong>Total: ${calculateTotal()}</strong>
                    </div>
                    <button className="pay-now-btn" onClick={() => alert("Proceed to Payment")}>Pay Now</button>
                </div>
            ) : (
                <p>Your cart is empty.</p>
            )}
        </div>
    );
}

export default Checkout;
