import React from 'react';
import { useCart } from '../context/CartContext';
import './Checkout.css';  // Import your CSS for styling

function Checkout() {
    const { cart } = useCart();  // Access the cart from the context

    return (
        <div className="checkout-container">
            <h1>Checkout</h1>
            {cart.items.length > 0 ? (
                <div>
                    <ul className="checkout-list">
                        {cart.items.map((item) => (
                            <li key={item._id} className="checkout-item">
                                <img src={item.image} alt={item.title} className="checkout-item-image"/>
                                <div className="item-details">
                                    <h3>{item.title}</h3>
                                    <p>Quantity: {item.quantity}</p>
                                    <p>Price: ${item.price}</p>
                                    <p>Total: ${item.quantity * item.price}</p>
                                </div>
                            </li>
                        ))}
                    </ul>
                    {/* Here, you can add form inputs for payment details and shipping address */}
                </div>
            ) : (
                <p>Your cart is empty.</p>
            )}
        </div>
    );
}

export default Checkout;
