import React, { useState, useEffect } from 'react';
import { useApi } from '../hooks/useApi';  // Make sure this hook is properly implemented

function AdminPanel() {
    const { fetchItems, updateItem, fetchOrders } = useApi();
    const [items, setItems] = useState([]);
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setLoading(true);
        fetchItems().then(data => {
            setItems(data);
            setLoading(false);
        });
        fetchOrders().then(data => {
            setOrders(data);
            setLoading(false);
        });
    }, [fetchItems, fetchOrders]);

    const handleUpdateStock = (itemId, newStock) => {
        updateItem(itemId, { stock: newStock }).then(() => {
            // Re-fetch items to update the list
            fetchItems().then(setItems);
        });
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            <h2>Orders</h2>
            {loading ? <p>Loading orders...</p> : (
                <ul>
                    {orders.map(order => (
                        <li key={order._id}>
                            Order ID: {order._id}, Total: {order.total}, User: {order.user.username}
                        </li>
                    ))}
                </ul>
            )}
            <h2>Stock Management</h2>
            {loading ? <p>Loading items...</p> : (
                <ul>
                    {items.map(item => (
                        <li key={item._id}>
                            {item.name} - In Stock: {item.stock}
                            <button onClick={() => handleUpdateStock(item._id, item.stock + 1)}>Increase Stock</button>
                            <button onClick={() => handleUpdateStock(item._id, item.stock - 1)}>Decrease Stock</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default AdminPanel;
