import React, { useState, useEffect } from 'react';
import { fetchItems, updateItem, fetchOrders } from '../services/ApiService';
import { useAuth } from '../context/authContext';

function AdminPanel() {
    const [items, setItems] = useState([]);
    const [orders, setOrders] = useState([]);
    const { user, token } = useAuth(); // Directly destructure token for clarity
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function loadData() {
            if (token) { // Check directly on token
                console.log('Fetching items and orders with token:', token);
                setLoading(true);
                try {
                    const fetchedItemsResponse = await fetchItems(token);
                    const fetchedOrdersResponse = await fetchOrders(token);
                    setItems(fetchedItemsResponse.data);
                    setOrders(fetchedOrdersResponse.data);
                    console.log('Items fetched:', fetchedItemsResponse.data);
                    console.log('Orders fetched:', fetchedOrdersResponse.data);
                } catch (error) {
                    console.error('Failed to fetch data:', error);
                } finally {
                    setLoading(false);
                }
            } else {
                console.log('No token found, unable to fetch data.');
                setLoading(false);
            }
        }

        loadData();
    }, [token]); // Depend directly on token

    const handleStockChange = async (itemId, newStock) => {
        if (newStock >= 0) {
            try {
                await updateItem(itemId, { stock: newStock }, token);
                setItems(prevItems => prevItems.map(item =>
                    item._id === itemId ? { ...item, stock: newStock } : item
                ));
            } catch (error) {
                console.error('Failed to update item:', error);
            }
        }
    };

    return (
        <div>
            <h1>Admin Panel</h1>
            {loading ? <p>Loading...</p> : (
                <>
                    <h2>Items</h2>
                    {items.length > 0 ? (
                        <ul>
                            {items.map(item => (
                                <li key={item._id}>
                                    {item.title} - Stock: {item.stock}
                                    <button onClick={() => handleStockChange(item._id, item.stock + 1)}>Increase</button>
                                    <button onClick={() => handleStockChange(item._id, item.stock - 1)}>Decrease</button>
                                </li>
                            ))}
                        </ul>
                    ) : <p>No items to display.</p>}
                    <h2>Orders</h2>
                    {orders.length > 0 ? (
                        <ul>
                            {orders.map(order => (
                                <li key={order._id}>
                                    Order ID: {order._id}, Total: ${order.total}, User ID: {order.userId}
                                </li>
                            ))}
                        </ul>
                    ) : <p>No orders to display.</p>}
                </>
            )}
        </div>
    );
}

export default AdminPanel;
