import React, { useState } from 'react';

function PaymentModal({ onSubmit, onClose }) {
    const [email, setEmail] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [cvv, setCvv] = useState('');
    const [expirationDate, setExpirationDate] = useState('');
    const [shippingAddress, setShippingAddress] = useState('');

    const handleSubmission = () => {
        // Validate inputs before submitting
        if (!/^\S+@\S+\.\S+$/.test(email)) {
            alert('Please enter a valid email address.');
            return;
        }
        if (!/^\d{16}$/.test(cardNumber)) {
            alert('Card number must be 16 digits.');
            return;
        }
        if (!/^\d{3}$/.test(cvv)) {
            alert('CVV must be 3 digits.');
            return;
        }
        if (!expirationDate) {
            alert('Please enter a valid expiration date.');
            return;
        }
        if (!shippingAddress) {
            alert('Shipping address cannot be empty.');
            return;
        }

        // Submit the payment information
        onSubmit({ email, cardNumber, cvv, expirationDate, shippingAddress });
    };

    return (
        <div className="modal">
            <div className="modal-content">
                <span className="close" onClick={onClose}>&times;</span>
                <h2>Enter Payment Details</h2>
                <input type="text" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email"/>
                <input type="text" value={cardNumber} onChange={e => setCardNumber(e.target.value)} placeholder="Card Number"/>
                <input type="text" value={cvv} onChange={e => setCvv(e.target.value)} placeholder="CVV"/>
                <input type="date" value={expirationDate} onChange={e => setExpirationDate(e.target.value)} placeholder="Expiration Date"/>
                <textarea value={shippingAddress} onChange={e => setShippingAddress(e.target.value)} placeholder="Shipping Address"></textarea>
                <button onClick={handleSubmission}>Process Order</button>
            </div>
        </div>
    );
}

export default PaymentModal;
