import React, { useState, useEffect } from 'react';
import { fetchItems, updateItem, fetchOrders } from '../services/ApiService';
import { useAuth } from '../context/authContext';

function AdminPanel() {
    const [items, setItems] = useState([]);
    const [orders, setOrders] = useState([]);
    const { user, token } = useAuth(); // Directly destructure token for clarity
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function loadData() {
            if (token) {
                try {
                    const fetchedItemsResponse = await fetchItems(token);
                    const fetchedOrdersResponse = await fetchOrders(token);
                    setItems(fetchedItemsResponse.data);
                    setOrders(fetchedOrdersResponse.data);
                } catch (error) {
                    console.error('Failed to fetch data:', error);
                }
            } else {
                console.log('No token found, unable to fetch data.');
            }
        }
        loadData();
    }, [token]);

    return (
        <div>
            <h1>Admin Panel</h1>
            <h2>Orders</h2>
            {orders.length > 0 ? (
                <ul>
                    {orders.map(order => (
                        <li key={order._id}>
                            Order ID: {order._id}, Total: ${order.total}, User: {order.user.username}
                            {order.items.map(({ item, quantity }) => (
                                <div key={item._id}>
                                    Item: {item.title}, Price: ${item.price}, Quantity: {quantity}
                                </div>
                            ))}
                        </li>
                    ))}
                </ul>
            ) : <p>No orders to display.</p>}
            <h2>Items</h2>
            {items.length > 0 ? (
                <ul>
                    {items.map(item => (
                        <li key={item._id}>
                            {item.title} - In Stock: {item.stock}
                        </li>
                    ))}
                </ul>
            ) : <p>No items to display.</p>}
        </div>
    );
}

export default AdminPanel;
